import React, { useRef } from "react";

import "./index.css";

const Visualizer2: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const analyser = useRef<AnalyserNode | null>(null);
  const worker = useRef<Worker | null>(null);

  let animationController: number | null;

  const handleAudioPlay = async () => {
    const audioContext = new AudioContext();
    if (!analyser.current) {
      try {
        // Get audio stream from mic input
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const audioSrc = audioContext.createMediaStreamSource(stream);
        analyser.current = audioContext.createAnalyser();
        audioSrc.connect(analyser.current);
        analyser.current.connect(audioContext.destination);
        // Initialize worker
        worker.current = new Worker("visualizer-worker.js");
        worker.current.postMessage({ type: "init", bufferLength: analyser.current.frequencyBinCount });
        worker.current.onmessage = ({ data }) => {
          if (data.type === "draw") {
            draw(data.dataArray);
          }
        };
      } catch (error) {
        console.error(error);
      }
    }
    visualizeData();
  };

  const visualizeData = () => {
    animationController = window.requestAnimationFrame(visualizeData);
    if (!analyser.current || !worker.current) {
      return cancelAnimationFrame(animationController!);
    }
    const canvas = canvasRef.current;
    if (!canvas) {
      return;
    }
    // const WIDTH = canvas.width;
    // const HEIGHT = canvas.height;
    const bufferLength = analyser.current.frequencyBinCount;
    const dataArray = new Float32Array(bufferLength);
    analyser.current.getFloatTimeDomainData(dataArray);
    worker.current.postMessage({ type: "compute", dataArray });
  };

  const draw = (dataArray: Float32Array) => {
    const canvas = canvasRef.current;
    if (!canvas) {
      return;
    }
    const ctx = canvas.getContext("2d");
    if (!ctx) {
      return;
    }
    const WIDTH = canvas.width;
    const HEIGHT = canvas.height;
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, WIDTH, HEIGHT);
    ctx.lineWidth = 2;
    ctx.strokeStyle = "green";
    ctx.beginPath();
    const sliceWidth = (WIDTH * 1.0) / dataArray.length;
    let x = 0;
    let y = (HEIGHT / 2);
    for (let i = 0; i < dataArray.length; i++) {
      const v = dataArray[i];
      const val = (v * 0.5);
      const yOffset = (val * HEIGHT);
      y = (HEIGHT / 2) + yOffset;
      if (i === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }
      x += sliceWidth;
    }
    ctx.stroke();
  };

  return (
    <>
      <button onClick={handleAudioPlay}>begin</button>
      <canvas ref={canvasRef} width={500} height={500} />
    </>
  );
};

export default Visualizer2;
