export function createAudioContext(): AudioContext {
  return new AudioContext();
}

export async function getAudioStream(): Promise<MediaStream> {
  return await navigator.mediaDevices.getUserMedia({ audio: true });
}

export function createAnalyzerNode(audioContext: AudioContext): AnalyserNode {
  const analyser = audioContext.createAnalyser();
  analyser.fftSize = 2048;
  analyser.smoothingTimeConstant = 0.8;
  return analyser;
}

export function connectAudioStream(audioSrc: MediaStreamAudioSourceNode, analyser: AnalyserNode, destination: AudioNode) {
  if (destination) {
    audioSrc.connect(analyser);
    analyser.connect(destination);
  } else {
    audioSrc.connect(analyser);
  }
}
