interface MessagePayload {
  analyser: AnalyserNode;
  canvasWidth: number;
  canvasHeight: number;
  bufferLength: number;
}

let canvasWidth: number,
  canvasHeight: number,
  bufferLength: number,
  dataArray: Float32Array,
  sliceWidth: number;

onmessage = function (e: MessageEvent) {
  switch (e.data.type) {
    case "init":
      const {
        canvasWidth: width,
        canvasHeight: height,
        bufferLength: bufLen,
      } = e.data.payload;
      canvasWidth = width;
      canvasHeight = height;
      bufferLength = bufLen;
      dataArray = new Float32Array(bufferLength);
      sliceWidth = (canvasWidth * 1.0) / bufferLength;
      break;

    case "start":
      draw(e.data.payload as MessagePayload);
      break;

    default:
      break;
  }
};

function draw(data: MessagePayload) {
  const { analyser, canvasWidth, canvasHeight, bufferLength } = data;

  analyser.getFloatTimeDomainData(dataArray);

  const canvas = self as unknown as WorkerGlobalScope & typeof globalThis & { canvas: HTMLCanvasElement };
  const ctx = canvas.canvas.getContext("2d");

  if (!ctx) {
    return;
  }

  ctx.fillStyle = "black";
  ctx.fillRect(0, 0, canvasWidth, canvasHeight);

  ctx.lineWidth = 2;
  ctx.strokeStyle = "green";

  ctx.beginPath();

  let x = 0;
  let y = canvasHeight / 2;

  for (let i = 0; i < bufferLength; i++) {
    const v = dataArray[i];
    const val = v * 0.5;
    const yOffset = val * canvasHeight;
    y = canvasHeight / 2 + yOffset;

    if (i === 0) {
      ctx.moveTo(x, y);
    } else {
      ctx.lineTo(x, y);
    }
    x += sliceWidth;
  }
  ctx.stroke();
  postMessage({ type: "draw" });
}

export {};
