import React, { useState, useEffect, useRef } from "react";

const AudioVisualizer: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const [audioContext, setAudioContext] = useState<AudioContext | null>(null);
  const [analyserNode, setAnalyserNode] = useState<AnalyserNode | null>(null);
  const [dataArray, setDataArray] = useState<Uint8Array | null>(null);

  // Define a custom Window type that includes `webkitAudioContext`
  interface CustomWindow extends Window {
    AudioContext: typeof AudioContext;
    webkitAudioContext?: typeof AudioContext;
  }

  const startAudioContext = () => {
    if (audioContext) {
      audioContext.resume().then(() => {
        console.log('AudioContext is now started');
      });
    }
  };

  useEffect(() => {
    const initAudio = async () => {
      try {
        const audioContext =
          (window as CustomWindow).AudioContext ||
          (window as CustomWindow).webkitAudioContext;
        const audioCTX = new audioContext();
        await audioCTX.resume(); // Resume the AudioContext as soon as possible
        const analyserNode = audioCTX.createAnalyser();
        analyserNode.fftSize = 2048;
        const bufferLength = analyserNode.frequencyBinCount;
        const dataArray = new Uint8Array(bufferLength);
        const source = audioCTX.createMediaStreamSource(
          await navigator.mediaDevices.getUserMedia({ audio: true })
          );
          source.connect(analyserNode);
          setAudioContext(audioCTX);
          setAnalyserNode(analyserNode);
          setDataArray(dataArray);
        } catch (err) {
          console.error(err);
        }
      };
      initAudio();
    }, []);

  useEffect(() => {
    if (audioContext && analyserNode && dataArray) {
      const canvas = canvasRef.current;
      const ctx = canvas?.getContext("2d");
      // audioContext.resume()

      const draw = () => {
        requestAnimationFrame(draw);
        analyserNode.getByteTimeDomainData(dataArray);
        ctx?.fillRect(0, 0, canvas?.width ?? 0, canvas?.height ?? 0);
        ctx?.beginPath();
        const sliceWidth = (canvas?.width ?? 0) / dataArray.length;
        let x = 0;
        for (let i = 0; i < dataArray.length; i++) {
          const v = dataArray[i] / 128.0;
          const y = (v * (canvas?.height ?? 0)) / 2;
          if (i === 0) {
            ctx?.moveTo(x, y);
          } else {
            ctx?.lineTo(x, y);
          }
          x += sliceWidth;
        }
        ctx?.stroke();
      };
      draw();
    }
  }, [audioContext, analyserNode, dataArray]);

  return (
    <>
  <canvas ref={canvasRef} width={600} height={300} />
    </>
  );
};

export default AudioVisualizer;
