import React, { useState, useEffect, useMemo } from "react";
import io from "socket.io-client";

import { start, stop } from "../util/speechRecognition";

import "./index.css";

const SERVER = "http://127.0.0.1:8001";
//Temporary fix?
const socket = io(SERVER, {
  transports: ["websocket"],
});

declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

const VoiceBot: React.FC = () => {
  const [listening, setListening] = useState(false);

  console.log("listening: ", listening);

  const SpeechRecognition =
    window.SpeechRecognition || window.webkitSpeechRecognition;

  const recognition = useMemo(
    () => new SpeechRecognition(),
    [SpeechRecognition]
  );

  recognition.lang = "en-US";
  recognition.interimResults = false;
  recognition.continuous = true;

  // fn to receive msg from server
  const botSpeak = (text: string | undefined) => {
    //  .speechSynthesis (returns obj --> entry point into Web Speech API)
    const synth = window.speechSynthesis;
    //                    SpeechSynthesisUtterance object
    const utterance = new SpeechSynthesisUtterance(text);
    synth.speak(utterance);
    // Note: it's possible to have infinite loop if the bot message includes keywords
  };

  const handleStartButton = () => {
    if (listening) {
      stop();
    } else {
      start();
    }
    setListening(!listening);
  };

  // const handleClick = () => {
  //   if (listening) {
  //     recognition.stop();
