const Visualizer3: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const audioRef = useRef<HTMLAudioElement | null>(null);
  const source = useRef<MediaElementAudioSourceNode | null>(null);
  const analyser = useRef<AnalyserNode | null>(null);
  const animationController = useRef<number | null>(null);
  const audioContext = useRef<AudioContext>(new AudioContext());

  const handleAudioPlay = async () => {
    // if (!audioRef.current) return;
    if (!source.current || !analyser.current) {
      try {
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const audioSrc = audioContext.current.createMediaStreamSource(stream);
        analyser.current = audioContext.current.createAnalyser();
        audioSrc.connect(analyser.current);
        analyser.current.connect(audioContext.current.destination);
      } catch (error) {
        console.error(error);
      }
    }
    visualizeData();
  };

  const visualizeData = () => {
    animationController.current = window.requestAnimationFrame(visualizeData);
    if (audioRef.current?.paused) {
      return cancelAnimationFrame(animationController.current!);
    }
    const songData = new Uint8Array(140);
    analyser.current?.getByteFrequencyData(songData);
    const bar_width = 3;
    let start = 0;
    const canvas = canvasRef.current;
    if (canvas) {
      const ctx = canvas.getContext("2d");
      ctx?.clearRect(0, 0, canvas.width, canvas.height);
      for (let i = 0; i < songData.length; i++) {
        // compute x coordinate where we would draw
        start = i * 4;
        //create a gradient for the  whole canvas
        let gradient = ctx?.createLinearGradient(
          0,
          0,
          canvas.width,
          canvas.height
        );
        gradient?.addColorStop(0.2, "#2392f5");
        gradient?.addColorStop(0.5, "#fe0095");
        gradient?.addColorStop(1.0, "purple");
        (ctx as CanvasRenderingContext2D).fillStyle = gradient!;
        (ctx as CanvasRenderingContext2D).fillRect(
          start,
          canvas.height,
          bar_width,
          -songData[i]
        );
      }
    }
  };

  return (
    <>
      <button onClick={handleAudioPlay}>begin</button>
      <canvas ref={canvasRef} />
    </>
  );
};
