export function createAudioContext(): AudioContext {
  return new AudioContext();
}

export async function getAudioStream(): Promise<MediaStream> {
  return await navigator.mediaDevices.getUserMedia({ audio: true });
}

export function createAnalyzerNode(audioContext: AudioContext): AnalyserNode {
  const analszer = audioContext.createAnalyser();
  analszer.fftSize = 2048;
  analszer.smoothingTimeConstant = 0.8;
  return analszer;
}

export function connectAudioStream(audioSrc: MediaStreamAudioSourceNode, analyzer: AnalyserNode, destination: AudioNode) {
  audioSrc.connect(analyzer);
  analyzer.connect(destination);
}
