import React, { useState, useEffect, useMemo } from "react";
import io from "socket.io-client";

import "./index.css";

const SERVER = "http://127.0.0.1:8001";
//Temporary fix?
const socket = io(SERVER, {
  transports: ["websocket"],
});

declare global {
  interface Window {
    SpeechRecognition: any;
    webkitSpeechRecognition: any;
  }
}

const VoiceBot: React.FC = () => {
  const [listening, setListening] = useState(false);
  const SpeechRecognition =
    window.SpeechRecognition || window.webkitSpeechRecognition;

  const recognition = useMemo(
    () => new SpeechRecognition(),
    [SpeechRecognition]
  );

  recognition.lang = "en-US";
  recognition.interimResults = false;
  recognition.continuous = true;

  const handleClick = () => {
    if (listening) {
      recognition.stop();
      botSpeak("Thanks for dropping by!");
      setListening((prev) => !prev);
    } else {
      botSpeak("Yo! What is good?!");
      recognition.start();
      setListening((prev) => !prev);
    }
  };

  useEffect(() => {
    recognition.addEventListener("result", (e: { results: string | any[] }) => {
      //console.log(e.results); // e.results :SpeechRecognitionResult object
      const last = e.results.length - 1;
      const text = e.results[last][0].transcript;
      socket.emit("user message", text);
    });

    recognition.addEventListener("speechend", () => {
      if (listening) {
        recognition.stop();
      } else {
        return null;
      }
      setListening((prev) => !prev);
    });

    recognition.addEventListener("error", (e: { error: any }) => {
      console.log(e.error);
      recognition.stop();
    });

  }, [listening, recognition]);


  // receive message from server
  const botSpeak = (text: string | undefined) => {
    //  .speechSynthesis (returns obj --> entry point into Web Speech API)
    const synth = window.speechSynthesis;
    //                    SpeechSynthesisUtterance object --> Web Speech API
    const utterance = new SpeechSynthesisUtterance(text);

    synth.speak(utterance);
    // Note: it's possible to have infinite loop if the bot message includes keywords
  };

  socket.on("bot message", (answer) => {
    const { msg, link } = answer;
    botSpeak(msg);
    if (link) {
      window.open(link, "_blank");
    }
  });

  return (
    <button onClick={handleClick}>
      mic:
      {listening ? "Listening ..." : "Click to surf"}
    </button>
  );
};

export default VoiceBot;
