import React, { useRef, useState } from "react";

import "./index.css";

const Visualizer2: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const analyser = useRef<AnalyserNode | null>(null);

  const [isPlaying, setIsPlaying] = useState<boolean>(false);

  const handleAudioPlay = async () => {
    const audioContext = new AudioContext();
    if (!analyser.current) {
      try {
        // Get audio stream from mic input
        const stream = await navigator.mediaDevices.getUserMedia({ audio: true });
        const audioSrc = audioContext.createMediaStreamSource(stream);
        analyser.current = audioContext.createAnalyser();
        audioSrc.connect(analyser.current);
        analyser.current.connect(audioContext.destination);
      } catch (error) {
        console.error(error);
      }
    }
    setIsPlaying(true);
    renderFrame();
  };

  const renderFrame = () => {
    if (!analyser.current || !canvasRef.current) {
      return;
    }

    const ctx = canvasRef.current.getContext("2d");
    if (!ctx) {
      return;
    }

    const WIDTH = canvasRef.current.width;
    const HEIGHT = canvasRef.current.height;
    const bufferLength = analyser.current.frequencyBinCount;
    const dataArray = new Uint8Array(bufferLength);
    analyser.current.getByteTimeDomainData(dataArray);

    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, WIDTH, HEIGHT);
    ctx.lineWidth = 2;
    ctx.strokeStyle = "green";
    ctx.beginPath();
    const sliceWidth = (WIDTH * 1.0) / bufferLength;
    let x = 0;
    for (let i = 0; i < bufferLength; i++) {
      const v = dataArray[i] / 128.0;
      const y = (v * HEIGHT) / 2;
      if (i === 0) {
        ctx.moveTo(x, y);
      } else {
        ctx.lineTo(x, y);
      }
      x += sliceWidth;
    }
    ctx.stroke();

    if (isPlaying) {
      window.requestAnimationFrame(renderFrame);
    }
  };

  return (
    <>
      <button onClick={handleAudioPlay}>begin</button>
      <canvas ref={canvasRef} width={500} height={500} />
    </>
  );
};

export default Visualizer4;
