import React, { useEffect, useState } from "react";

import "./index.css";

const Visualizer2: React.FC = () => {
  const [stream, setStream] = useState<MediaStream | null>(null);
  const analyserCanvas: any = React.useRef(null);

  const audioTest = async () => {
    if (navigator.mediaDevices.getUserMedia !== null) {
      const options = {
        video: false,
        audio: true,
      };
      try {
        const stream = await navigator.mediaDevices.getUserMedia(options);
        const audioCtx = new AudioContext();
        const analyser = audioCtx.createAnalyser();
        analyser.fftSize = 1024;
        const audioSrc = audioCtx.createMediaStreamSource(stream);
        audioSrc.connect(analyser);
        const data = new Uint8Array(analyser.frequencyBinCount);
        const ctx = analyserCanvas.current.getContext("2d");

        const draw = (dataParm: any) => {
          ctx.clearRect(0, 0, analyserCanvas.current.width, analyserCanvas.current.height); // clear the canvas before drawing
          dataParm = [...dataParm];
          ctx.fillStyle = "white"; //white background
          ctx.lineWidth = 2; //width of candle/bar
          ctx.strokeStyle = "#d5d4d5"; //color of candle/bar
          const space = analyserCanvas.current.width / dataParm.length;

          dataParm.forEach((value: number, i: number) => {
            ctx.beginPath();
            ctx.moveTo(space * i, analyserCanvas.current.height); //x,y
            ctx.lineTo(space * i, analyserCanvas.current.height - value); //x,y
            ctx.stroke();
          });
        };
        const loopingFunction = () => {
          requestAnimationFrame(loopingFunction);
          analyser.getByteFrequencyData(data);
          draw(data);
        };
        requestAnimationFrame(loopingFunction);
        setStream(stream);
      } catch (err) {
        // error handling
      }
    }
  };

  useEffect(() => {
    audioTest();
  }, []);

  useEffect(() => {
    if (stream) {
      audioTest();
    }
  }, [stream]);

  return <canvas ref={analyserCanvas} />;
};

export default Visualizer2;
