import React, { useRef, useMemo, useEffect } from "react";
import {
  createAudioContext,
  createAnalyzerNode,
  connectAudioStream,
  getAudioStream,
} from "../utils/audioUtils";

import "./index.css";

const Visualizer2: React.FC = () => {
  const canvasRef = useRef<HTMLCanvasElement | null>(null);
  const analyser = useRef<AnalyserNode | null>(null);

  const handleAudioPlay = async () => {
    const audioContext = createAudioContext();
    if (!analyser.current) {
      try {
        // Get audio stream from mic input
        const stream = await getAudioStream();
        const audioSrc = audioContext.createMediaStreamSource(stream);
        analyser.current = createAnalyzerNode(audioContext);
        connectAudioStream(
          audioSrc,
          analyser.current,
          audioContext.destination
        );
      } catch (error) {
        console.error(error);
      }
    }
    worker.postMessage({ type: "start" });
  };

  const handleAudioStop = () => {
    worker.postMessage({ type: "stop" });
  };

  const handleWorkerMessage = (event: MessageEvent) => {
    const canvas = canvasRef.current;
    if (!canvas) {
      return;
    }
    const ctx = canvas.getContext("2d");
    if (!ctx) {
      throw new Error ('canvas')
    }
    const data = event.data;
    if (data.type === "draw") {
      const { dataArray, bufferLength, sliceWidth } = data;
      const WIDTH = canvas.width;
      const HEIGHT = canvas.height;
      ctx.fillStyle = "black";
      ctx.fillRect(0, 0, WIDTH, HEIGHT);
      ctx.lineWidth = 2;
      ctx.strokeStyle = "green";
      ctx.beginPath();
      let x = 0;
      let y = HEIGHT / 2;
      for (let i = 0; i < bufferLength; i++) {
        const v = dataArray[i];
        const val = v * 0.5;
        const yOffset = val * HEIGHT;
        y = HEIGHT / 2 + yOffset;
        if (i === 0) {
          ctx.moveTo(x, y);
        } else {
          ctx.lineTo(x, y);
        }
        x += sliceWidth;
      }
      ctx.stroke();
    }
  };
  const worker: Worker = useMemo(() => {
    const worker = new Worker(new URL("../worker/drawer.ts", import.meta.url));
    worker.onerror = (error) => error;
    return worker;
  }, []);

  // onmessage - recieve from getData worker
  useEffect(() => {
    if (window.Worker) {
      worker.onmessage = handleWorkerMessage;
    }
  }, [worker]);

  // const worker = new Worker("./visualizer.worker.ts");

  return (
    <div className="container">
      <canvas ref={canvasRef} />
      <div className="controls">
        <button onClick={handleAudioPlay}>Play</button>
        <button onClick={handleAudioStop}>Stop</button>
      </div>
    </div>
  );
};

export default Visualizer2;
